## 01


```cpp
#include <iostream>
#include <vector>
using namespace std;

const int MIN=0x80000000;
const int N=3;                //
const int V=5;                //
int f[N+1][V+1];

int Package(int *W,int *C,int N,int V);

int main()
{
    int W[4]= {0,7,5,8};      //
    int C[4]= {0,2,3,4};      //
    int result=Package(W,C,N,V);
    if(result>0)
    {
        cout<<endl<<"the opt value:"<<result<<endl;

        int i=N,j=V;
        while(i)
        {
            if(f[i][j]==(f[i-1][j-C[i]]+W[i]))
            {
                cout<<i<<":"<<"w="<<W[i]<<",c="<<C[i]<<endl;
                j-=C[i];
            }
            i--;
        }
    }
    else
        cout<<"can not find the opt value"<<endl;
    return 0;
}

int Package(int *W,int *C,int N,int V)
{
    int i,j;
    memset(f,0,sizeof(f));    //0

    for(i=0; i<=N; i++)
        for(j=1; j<=V; j++)   
        //
            f[i][j]=MIN;      
            //
            //0

    for(i=1; i<=N; i++)
        for(j=C[i]; j<=V; j++)
        {
            f[i][j]=(f[i-1][j] > f[i-1][j-C[i]]+W[i]) ? f[i-1][j] : (f[i-1][j-C[i]]+W[i]);
            cout<<"f["<<i<<"]["<<j<<"]="<<f[i][j]<<endl;
        }
    return f[N][V];
}
```


```cpp
#include <iostream>
#include <vector>
using namespace std;

const int MIN=0x80000000;
const int N=3;                //
const int V=5;                //
int f[V+1];

int Package(int *W,int *C,int N,int V);

int main()
{
	int W[4]= {0,7,5,8};      //
	int C[4]= {0,2,3,4};      //
	int result=Package(W,C,N,V);

	if(result>0)
	{
		cout<<endl;
		cout<<"the opt value:"<<result<<endl;
	}
	else
		cout<<"can not find the opt value"<<endl;
	return 0;
}
int Package(int *W,int *C,int N,int V)
{
	int i,j;
	memset(f,0,sizeof(f));    //0

	for(i=1; i<=V; i++)
    //
		f[i]=MIN;
        //
        //0

	for(i=1; i<=N; i++)
		for(j=V; j>=C[i]; j--)//
		{
			f[j]=(f[j] > f[j-C[i]]+W[i]) ? f[j] : (f[j-C[i]]+W[i]);
			cout<<"f["<<i<<"]["<<j<<"]="<<f[j]<<endl;
		}
	return f[V];
}
