#include<ctime>
#include<iostream>
using namespace std;

clock_t s, e;
int a;

inline int Read()
{
    int x = 0, f = 1;
    char ch = getchar();
    while (ch < '0' || ch > '9')
    {
        if (ch == '-')
            f = -1;
        ch = getchar();
    }
    while (ch >= '0' && ch <= '9')
    {
        x = (x << 1) + (x << 3) + (ch ^ 48);
        ch = getchar();
    }
    return x * f;
}

int main()
{
    //input.txt
    freopen("input.txt", "r+", stdin);

    //cin part
    cout << "cin takes ";
    s = clock();
    for (int i = 1; i <= 1000000; i++)    cin >> a;
    e = clock();
    cout << (double)(e - s) << endl;

    //scanf() part
    cout << "scanf() takes ";
    s = clock();
    for (int i = 1; i <= 1000000; i++)    scanf("%d", &a);
    e = clock();
    cout << (double)(e - s) << endl;

    //Read() part
    cout << "Read() takes ";
    s = clock();
    for (int i = 1; i <= 1000000; i++)    a = Read();
    e = clock();
    cout << (double)(e - s) << endl;

    return 0;
}
