#include <string>
using namespace std;

char str[260];

void init();
void replace_boy(char*, int);
void replace_girl(char*, int);

int main()
{
    cin >> str;
    init();
    int boy = 0, girl = 0;
    for (int i = 0, len = strlen(str); i < len; i++)
    {
        if (str[i] == 'a' || str[i] == 'b' || str[i] == 'o' || str[i] == 'y')
            boy++;
        else if (str[i] == 'z' || str[i] == 'g' || str[i] == 'i' || str[i] == 'r' || str[i] == 'l')
            girl++;
    }
    cout << boy << endl << girl;
    cin.get(); cin.get();
    return 0;
}
void init()
{
    //
    replace_boy("boy", 3);
    replace_boy("bo", 2);
    replace_boy("oy", 2);

    replace_girl("girl", 4);
    replace_girl("gir", 3);
    replace_girl("irl", 3);
    replace_girl("gi", 2);
    replace_girl("ir", 2);
    replace_girl("rl", 2);

}
void replace_boy(char* sub, int len)
{
    char *boy = strstr(str, sub);
    //str1str2
    while (boy != NULL)
    {
        *boy = 'a';
        for (int i = 1; i < len; i++)
            *(boy + i) = '.';
        boy = strstr(str, sub);//
    }
}
void replace_girl(char* sub, int len)
{
    char *girl = strstr(str, sub);
    while (girl != NULL)
    {
        *girl = 'z';
        for (int i = 1; i < len; i++)
            *(girl + i) = '.';
        girl = strstr(str, sub);
    }
}
```

