#include <iostream>
#include <cstring>
#include <cstdlib>
#include <cstdio>
using namespace std;

int n, m, edgenum, ans = 0;
bool dis[1010][1010] = {false}; //d
bool vis[1010] = {true};        //used
int seat[1010] = {0};           //

bool sit(int);

int main()
{
    cin >> n >> m >> edgenum;
    for (int i = 1; i <= edgenum; i++)
    {
        int u, v;
        cin >> u >> v;
        if (u > n || v > m) //
            continue;
        dis[u][v] = true;
    }
    for (int i = 1; i <= n; i++)
    {
        memset(vis, true, sizeof(vis));
        if (sit(i))
            ans++;
    }
    cout << ans;
    return 0;
}
bool sit(int a)
{
    if (a == 0)
        return true;
    for (int i = 1; i <= n; i++)
    {
        if (dis[i][a] && vis[i])
        {
            int t = seat[i];
            seat[i] = a;
            vis[i] = false;
            if (sit(t))
                return true;
            seat[i] = t;
        }
    }
    return false;
}
