using namespace std;

int wd, t;
double apx = 0;
map<double, bool> mem;

int main()
{
	cin >> wd;
	if (wd < 0)
	{
		printf("Num shouldn\'t be negative.");
		return 0;
	}
	for (apx = 1; apx * apx <= wd; apx++);
	apx--;
	// wd = apx * apx + 2 * apx * x
	getchar();
	while (true)
	{
		apx += (wd - apx * apx) / (2 * apx);
		if (mem[apx])
		{
			printf("This is my best...\n");
			break;
		}
		printf("sqrt(%d) = %.14lf", wd, apx);
		getchar();
		mem[apx] = true;
	}
	return 0;
}
